package conditionstatements;
import java.lang.*;
import java.util.*;

public class Test2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=15;
		int t=i;
		do {
			System.out.println(t);
			t=t+1;
			
		}while(t<=0);
		
		while(i<=10){
			System.out.println(i);
			i++;
		}

	}

}
